<?php/** Abdukodir Khojiyev* Backend Developer* Project: uzpost* Date:  13/03/25*/namespace Encoderuz\Uzpost\Http;use Encoderuz\Uzpost\Exceptions\UzpostException;use Encoderuz\Uzpost\Services\CustomerService;class Uzpost{    public static string $token;    public function __construct(private string $api_key)    {    }    public static function token(string $token): Uzpost    {        self::$token = $token;        return new self($token);    }    /**     * @throws UzpostException     */    public static function get_token(string $username, string $password)    {        $client = new UzpostClient();        $customer = new CustomerService($client);        $token_request = $customer->authenticate([            'username'    => $username,            'password'    => $password,            'remember_me' => true        ]);        self::$token = $token_request['data']['id_token'];        return self::$token;    }    /**     * @throws UzpostException     */    public function customer_sign_up(array $customerData)    {        $client = new UzpostClient(self::$token);        $customer = new CustomerService($client);        return $customer->sign_up($customerData);    }    /**     * @throws UzpostException     */    public function customer_data()    {        $client = new UzpostClient(self::$token);        $customer = new CustomerService($client);        return $customer->get_customer_data();    }}