<?php/** Abdukodir Khojiyev* Backend Developer* Project: uzpost* Date:  13/03/25*/namespace Encoderuz\Uzpost\Http;use Encoderuz\Uzpost\Exceptions\UzpostException;use Encoderuz\Uzpost\Services\CustomerService;class Uzpost{    public function __construct(private string $api_key)    {    }    public static function token(string $token): Uzpost    {        return new self($token);    }    /**     * @throws UzpostException     */    public static function get_token(string $username, string $password)    {        $client = new UzpostClient();        $customer = new CustomerService($client);        $token_request = $customer->authenticate([            'username'    => $username,            'password'    => $password,            'remember_me' => true        ]);        return $token_request['data']['id_token'];    }}