<?php/** Abdukodir Khojiyev* Backend Developer* Project: uzpost* Date:  11/03/25*/namespace Encoderuz\Uzpost\Services;use Encoderuz\Uzpost\Contracts\OrderServiceInterface;use Encoderuz\Uzpost\Exceptions\UzpostException;use Encoderuz\Uzpost\Http\UzpostClient;class OrderService implements OrderServiceInterface{    private UzpostClient $client;    public function __construct(        UzpostClient $client    ) {        $this->client = $client;    }    /**     * @throws UzpostException     */    public function track_order(string $orderNumber): array    {        return $this->client->send_request('GET', "public/order/$orderNumber");    }    /**     * @throws UzpostException     */    public function get_order_details(string $orderId, string $mobile): array    {        return $this->client->send_request('GET', "customer/order/$orderId/$mobile");    }    /**     * @throws UzpostException     */    public function get_customer_orders(int $page, int $size): array    {        return $this->client->send_request('GET', "customer/orders", ['page' => $page, 'size' => $size]);    }    /**     * @throws UzpostException     */    public function create_order(array $orderData): array    {        return $this->client->send_request("POST", "customer/order", $orderData);    }    /**     * @throws UzpostException     */    public function cancel_order(string $barcode): array    {        return $this->client->send_request("PUT", "customer/order/cancel/$barcode");    }}