<?php/** Abdukodir Khojiyev* Backend Developer* Project: uzpost* Date:  11/03/25*/namespace Encoderuz\Uzpost\Services;use Encoderuz\Uzpost\Contracts\PricingServiceInterface;use Encoderuz\Uzpost\Exceptions\UzpostException;use Encoderuz\Uzpost\Http\UzpostClient;class PricingService implements PricingServiceInterface{    protected UzpostClient $client;    public function __construct(        UzpostClient $client    ) {        $this->client = $client;    }    /**     * @throws UzpostException     */    public function get_package_prices(array $queryParams): array    {        return $this->client->send_request('GET', 'customer/packages/prices/starting_from', $queryParams, 'v2');    }}